import React, { useState, useRef, useEffect } from "react";
import "../SearchPage.css";
import { searchTextApi } from "../api"; // ✅ videoStreamApi 제거

const SearchPage: React.FC = () => {
  const [query, setQuery] = useState("");
  const [textResults, setTextResults] = useState<any[]>([]);
  const videoRefs = useRef<(HTMLVideoElement | null)[]>([]);

  const handleSearch = async () => {
    console.log("검색어:", query);

    try {
      const res = await fetch(searchTextApi(query));
      if (!res.ok) throw new Error("검색 API 오류");
      const data = await res.json();

      if (data.results && data.results.length > 0) {
        // ✅ 백엔드에서 내려온 video_url 그대로 사용
        setTextResults(data.results);
      } else {
        setTextResults([]);
      }
    } catch (err) {
      console.error(err);
    }
  };

  // ✅ 검색 결과가 바뀔 때마다 자동재생 시도
  useEffect(() => {
    videoRefs.current.forEach((video) => {
      if (video) {
        video.play().catch((err) => {
          console.warn("자동재생 실패:", err);
        });
      }
    });
  }, [textResults]);

  return (
    <div className="search-container">
      {/* 검색 박스 */}
      <div className="search-box">
        <input
          type="text"
          placeholder="검색어를 입력하세요..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 검색 결과 */}
      <div className="results-grid">
        <div className="results-column">
          <h2>텍스트 → 텍스트 결과</h2>
          {textResults.map((item, idx) => (
            <div key={idx} className="result-card">
              <p>{item.text}</p>

              {item.video_url && (
                <video
                  ref={(el) => {
                    videoRefs.current[idx] = el;
                  }}
                  width="320"
                  autoPlay
                  muted
                  loop
                  playsInline
                  controls
                >
                  <source src={item.video_url} type="video/mp4" />
                  지원하지 않는 브라우저입니다.
                </video>
              )}
            </div>
          ))}
        </div>

        <div className="results-column">
          <h2>텍스트 → 이미지 결과</h2>
          <div className="result-card">이미지 결과 (추후 연결)</div>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
