import React, { useState } from "react";
import "../SearchPage.css";
import { searchTextApi, videoStreamApi } from "../api";

const SearchPage: React.FC = () => {
  const [query, setQuery] = useState("");
  const [textResults, setTextResults] = useState<any[]>([]); // ✅ 검색 결과 저장
  const [videoUrl, setVideoUrl] = useState<string | null>(null); // ✅ 비디오 URL

  const handleSearch = async () => {
    console.log("검색어:", query);

    try {
      // ✅ 1) 텍스트 검색 API 호출
      const res = await fetch(searchTextApi(query));
      if (!res.ok) throw new Error("검색 API 오류");
      const data = await res.json();

      // 예시: [{ session_id: "20240101", camera_id: "22008760", text: "로봇이 블록을 집는다" }]
      setTextResults(data.results || []);

      // ✅ 2) 첫 번째 결과의 sessionId + cameraId 로 비디오 URL 생성
      if (data.results && data.results.length > 0) {
        const first = data.results[0];
        setVideoUrl(videoStreamApi(first.session_id, first.camera_id));
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="search-container">
      {/* 검색 박스 */}
      <div className="search-box">
        <input
          type="text"
          placeholder="검색어를 입력하세요..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 검색 결과 */}
      <div className="results-grid">
        <div className="results-column">
          <h2>텍스트 → 텍스트 결과</h2>
          {textResults.map((item, idx) => (
            <div key={idx} className="result-card">
              <p>{item.text}</p>

              {/* ✅ 비디오도 같이 보여줌 */}
              {videoUrl && (
                <video width="300" controls>
                  <source src={videoUrl} type="video/mp4" />
                  지원하지 않는 브라우저입니다.
                </video>
              )}
            </div>
          ))}
        </div>

        <div className="results-column">
          <h2>텍스트 → 이미지 결과</h2>
          <div className="result-card">이미지 결과 (추후 연결)</div>
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
